import { Ionicons } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import { useEffect, useState } from "react";
import { Pressable, ScrollView, Text, View } from "react-native";
import { Avatar } from "react-native-paper";

export default function PayRoll({route})
{
  const HR = route.params
    const navigation=useNavigation();
    const [Employee,setEmployee]=useState([]);

    useEffect(()=>{
        fetchData();


    },[]);

    const fetchData = async () => {
        try {
          const response = await fetch(`${process.env.API_URL}/Employee/hr/${HR}`);
          if (response.ok) {
            const data = await response.json();
            setEmployee(data);
          
          } else {
            console.error("Errorrr:", response.status, response.statusText);
          }
        }
        
        catch (error) {
          console.error("Error:", error);
        }
      };

      const fetchAttendance = async (employeeId) => {
        try {
          const response = await fetch(`${process.env.API_URL}/Attendance/${employeeId}`);
          if (response.ok) {
            const data = await response.json();
            return data;
          } else {
            console.error("Error:", response.json());
            return null;
          }
        } 
        catch (error) {
          console.error("Error fetching attendance data:", error);
          return null;
        }
      };

      const fetchPayment = async (employeeId) => {
        try {
          const response = await fetch(`${process.env.API_URL}/Payment/${employeeId}`);
          if (response.ok) {
            const data = await response.json();
            return data;
          } else {
            console.error("Error:", response.json());
            return null;
          }
        } catch (error) {
          console.error("Error fetching attendance data:", error);
          return null;
        }
      };

      const handleCalculate = async (employee) => {
      //  console.log(employee)
        const attendanceData = await fetchAttendance(employee._id);
        const attendanceID=attendanceData[0]._id
        console.log(attendanceID)
        const PaymentData = await fetchPayment(employee._id);
        //console.log(PaymentData)
        const pay=PaymentData[0];
       // console.log(pay.Basicsalary)
      
        navigation.navigate('Daterange', { employee, attendanceID ,Payment:PaymentData});
      };

      

     
      return (
        <ScrollView style={{backgroundColor:"white"}}>
          <View style={{ marginTop: 50,marginHorizontal: 10,flexDirection: "row",justifyContent: "flex-start", alignItems: "center",margin:20}}>

          <Pressable onPress={() => navigation.navigate("Home")} style={{backgroundColor:"#66a4fd",padding:3,borderRadius:20}}>
            <Ionicons name="arrow-back" size={24} color="white" />
          </Pressable>
          <Text style={{ fontSize: 22, fontWeight: "600",color: "rgba(131, 112, 223,1)",flex: 1,  textAlign: "center"}}>EMPLOYEE LIST</Text>



        </View>
          {Employee.map((employee, index) => (
            <View key={index} style={{ marginLeft:10,marginLeft:10,margin:3, padding: 2, backgroundColor: '#66a4fd', borderRadius: 10,flexDirection:"row",justifyContent:"space-between",alignItems:"center"}}>

                <View style={{flexDirection:"row",justifyContent:"center",alignItems:"center"}}>
                <Avatar.Image
              size={60}
              style={{margin:5}}
              source={{
                uri:
                 !employee
                    ?'data:image/png;base64,'
                    : employee.image,
              }}
            />
                <View>
                <Text style={{fontSize:20,fontWeight:600,color:"white",marginHorizontal:10}}>{employee.Name}</Text>
                <Text style={{fontSize:10,fontWeight:600,color:"white",marginHorizontal:10}}>{employee._id}</Text>

                </View>
                
                

                </View>
       
              <View style={{alignItems:"center",justifyContent:"center",margin:5}}>

              <Pressable onPress={()=>handleCalculate(employee)} >
                <View style={{ backgroundColor: '#156D7E', padding: 10, borderRadius: 5 }}>
                  <Text style={{ color: 'white' }}>Calculate</Text>
                </View>
              </Pressable>

              </View>
             
            </View>
          ))}
        </ScrollView>
      );
    }




  

   


